import * as React from "react";
import Box from "@mui/material/Box";
import Button from "@mui/material/Button";
import ButtonGroup from "@mui/material/ButtonGroup";
import { useState, useEffect } from "react";

export const useOutsideClick = (el, initialState) => {
  const [isActive, setIsActive] = useState(initialState)

  useEffect(() => {
    const onClick = e => {
      if (el.current !== null && !el.current.contains(e.target)){
        setIsActive(!isActive);
      }
    }

    if(isActive) {
      window.addEventListener("click", onClick)
    }
    return () => {
      window.removeEventListener("click", onClick)
    }
  }, [isActive, el])

  return [isActive, setIsActive]
}

const renderRef = useRef(null);
const [isActive, setIsActive] = useOutsideClick(renderRef)
const onClick = () => setIsActive(!isActive)

export default function Menu() {
  return (
    <Box
      sx={{
        display: "flex",
        alignItems: "top",
        textAlign: "center",
        bgcolor: "black",
        borderRadius: 5,
        color: "white",
        justifyContent: "space-between",
        padding: "15px 15px 15px 15px",
      }}
    >
      <Box>
        <ButtonGroup sx={{ color: "#fff" }} variant="outlined" aria-label="outlined button group">
          <Button ref={renderRef} >About Me</Button>
          <Button >Projects</Button>
          <Button >Contact</Button>
        </ButtonGroup>
      </Box>
    </Box>

  );
}
