import * as React from 'react';
import Avatar from '@mui/material/Avatar';
import CssBaseline from '@mui/material/CssBaseline';
import Link from '@mui/material/Link';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import Container from '@mui/material/Container';
import { createTheme, ThemeProvider } from '@mui/material/styles';

function Copyright(props) {
  return (
    <Typography variant="body2" color="text.secondary" align="center" {...props}>
      {'Copyright © '}
      <Link color="inherit" href="https://github.com/joaovictorlernermartins">
        João Victor Lerner Martins
      </Link>{' '}
      {new Date().getFullYear()}
      {'.'}
    </Typography>
  );
}

const defaultTheme = createTheme();

export default function App() {
  return (
    <ThemeProvider theme={defaultTheme}>
      <Container component="main">
        <CssBaseline />
        <Box sx={{
          marginTop: 12,
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
        }}>
          <Avatar
            alt="Foto de Perfil do João"
            src="https://avatars.githubusercontent.com/u/81587343?v=4"
            sx={{ width: 200, height: 200 }}
          />
          <Typography sx={{marginTop: 2}} component="h1" variant="h5">
            João Victor Lerner Martins
          </Typography>
          <Typography component="h1" variant="subtitle1">
          Hi, I'm a Jr. developer. I'm 20 years old and I live in Brazil
          </Typography>
        </Box>
        <Copyright sx={{ mt: 8, mb: 4 }} />
      </Container>
    </ThemeProvider>
  );
}
