import * as React from "react";
import Box from "@mui/material/Box";
import Button from "@mui/material/Button";
import ButtonGroup from "@mui/material/ButtonGroup";
import { useState, useEffect } from "react";





export default function Menu() {

  const renderRef = React.useRef(null)

  const [aboutMeActive, setAboutMeActive] = useState(true)

  const AboutMe = () => setAboutMeActive(!aboutMeActive)
  console.log(aboutMeActive)

  return (
    <Box
      sx={{
        display: "flex",
        alignItems: "top",
        textAlign: "center",
        bgcolor: "black",
        borderRadius: 5,
        color: "white",
        justifyContent: "space-between",
        padding: "15px 15px 15px 15px",
      }}
    >
      <Box>
        <ButtonGroup sx={{ color: "#fff" }} variant="outlined" aria-label="outlined button group">
          <Button ref={renderRef} onClick={AboutMe} >About Me</Button>
          <Button >Projects</Button>
          <Button >Contact</Button>
        </ButtonGroup>
      </Box>
    </Box>

  );
}
