import * as React from 'react';
import Avatar from '@mui/material/Avatar';
import CssBaseline from '@mui/material/CssBaseline';
import Link from '@mui/material/Link';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import Container from '@mui/material/Container';
import Grid from '@mui/material/Unstable_Grid2';
import { useTheme, ThemeProvider, createTheme } from '@mui/material/styles';


function Copyright(props) {
  return (
    <Typography variant="body2" color="text.secondary" align="center" {...props}>
      {'Copyright © '}
      <Link color="inherit" href="https://github.com/joaovictorlernermartins">
        João Victor Lerner Martins
      </Link>{' '}
      {new Date().getFullYear()}
      {'.'}
    </Typography>
  );
}

const ColorModeContext = React.createContext({ toggleColorMode: () => {} });

export default function App() {
  const theme = useTheme();
  const colorMode = React.useContext(ColorModeContext);
  return (
      <Container component="main">
        <CssBaseline />
        <Grid container sx={{marginTop:12, bgcolor:'#2d2d29'}} spacing={2} columns={16}>
          <Grid xs={4}>
            <Box sx={{
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            textAlign: 'center',
            bgcolor: 'background.default',
            color: 'text.primary',
            }}>
            <Avatar
              alt="Foto de Perfil do João"
              src="https://avatars.githubusercontent.com/u/81587343?v=4"
              sx={{ width: 200, height: 200 }}
            />
            <Typography component="h1" variant="h5">
              João Victor Martins
            </Typography>
            <Typography component="h1" variant="subtitle1">
            Hi, I'm a <strong>web developer</strong>, I'm 20 years old and I live in Brazil
            </Typography>

            </Box>
          </Grid>
          <Grid xs={12}>
              <IconButton sx={{ ml: 1 }} onClick={colorMode.toggleColorMode} color="inherit">
              {theme.palette.mode === 'dark' ? <Brightness7Icon /> : <Brightness4Icon />}
            </IconButton>
          </Grid>
        </Grid>
        <Copyright sx={{ mt: 8, mb: 4 }} />
      </Container>
  );
}
